import React from "react";
import { useLocation } from "react-router";

const Edit = () => {
  const location = useLocation();
  return (
    <div>
      <h1>게시글 수정</h1>
      <input type="text" value={location.state.content} size="10"></input>
    </div>
  );
};

export default Edit;
