import React from "react";
import { useLocation } from "react-router";

const Edit = () => {
  const location = useLocation();
  const handleSubmit = () => {};
  return (
    <div>
      <h1>게시글 수정</h1>
      <form>
        <textarea rows="5" cols="40" style={{ resize: "none" }}>
          {location.state.content}
        </textarea>
        <button onSubmit={() => handleSubmit()}>수정</button>
      </form>
    </div>
  );
};

export default Edit;
