import React, { useState } from "react";
import { useLocation } from "react-router";

const Edit = () => {
  const location = useLocation();
  const [content, useContent] = useState(location.state.content);
  const handleChange = () => {};
  const handleSubmit = () => {};
  return (
    <div>
      <h1>게시글 수정</h1>

      <textarea
        rows="5"
        cols="40"
        style={{ resize: "none" }}
        onChange={() => handleChange()}
      >
        {location.state.content}
      </textarea>
      <button onClick={() => handleSubmit()}>수정</button>
    </div>
  );
};

export default Edit;
